import React, { useState } from 'react';
import './Achievements.css';
import ssiImage from '../assests/ssi.png';
import certificateMain from '../assests/certificateMain.jpg';
import quasarCert from '../assests/quasar.jpg';
import Certificates from './Certificates';

const Achievements = () => {
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [activeSection, setActiveSection] = useState(null);

  const achievements = [
    {
      title: "Quasar National-Level Hackathon Finalist",
      description: "Built a Learning Path Dashboard (Top 60 out of 650+ teams)",
      image: quasarCert
    },
    {
      title: "CodeAthon Inter-College Hackathon",
      description: "Ranked 4th, competing among top teams, solving complex challenges with innovative solutions",
      image: ssiImage  // Now this will work since ssiImage is imported
    },
    {
      title: "Project Competition Winner",
      description: "1st Prize in Software Domain (2023-2024)",
      image: ssiImage
    },
    {
      title: "Technical Paper Presentation",
      description: "1st Position in State Level Competition (2023-2024)",
      image: ssiImage
    },
    {
      title: "State Level Elocution Competition",
      description: "1st Prize (2023-2024)",
      image: ssiImage
    },
    {
      title: "Pre-Secondary Scholarship",
      description: "133rd Rank in Maharashtra and 1st Rank in School (2018-2019)",
      image: ssiImage
    },
    {
      title: "NELTAS ECAT Grand Finale",
      description: "Certificate & Medal for Scoring 80% in National Level (2018-2019)",
      image: ssiImage
    }
];

  const certifications = [
    // Add your certifications here later
    {
      title: "Certification Placeholder",
      description: "Details will be added later",
      image: ssiImage
    }
  ];

  const handleCircleClick = (section) => {
    setActiveSection(section);
    setIsPopupOpen(true);
  };

  return (
    <section className="achievements" id="achievements">
      <div className="achievements-header">
        <h2>Achievements & Certifications</h2>
        <p>Milestones Along My Journey</p>
      </div>
      
      <div className="circles-container">
        <Certificates onCircleClick={handleCircleClick} />
        
        <div className="circle awards" onClick={() => handleCircleClick('achievements')}>
          <div className="circle-content">
            <h3>Achievements</h3>
          </div>
        </div>
      </div>

      {isPopupOpen && (
        <div className="popup-overlay" onClick={() => setIsPopupOpen(false)}>
          <div className="popup-content" onClick={e => e.stopPropagation()}>
            <button className="close-btn" onClick={() => setIsPopupOpen(false)}>×</button>
            <h2>{activeSection === 'certifications' ? 'Certifications' : 'Achievements'}</h2>
            <div className="cards-container">
              {(activeSection === 'certifications' ? certifications : achievements).map((item, index) => (
                <div className="achievement-card" key={index}>
                  <div className="card-image">
                    <img 
                      src={item.image} 
                      alt={item.title}
                      onError={(e) => {
                        console.error('Image failed to load:', item.image);
                        e.target.src = 'https://via.placeholder.com/400x300';
                      }}
                    />
                  </div>
                  <div className="card-content">
                    <h3>{item.title}</h3>
                    <p>{item.description}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </section>
  );
};

export default Achievements;
