import React from 'react';
import './About.css';

const About = () => {
  const education = [
    {
      institution: "Vidyalankar Institute of Technology, Wadala",
      degree: "3rd Semester (Direct Second Year Admission)",
      score: "10 CGPA"
    },
    {
      institution: "Bharati Vidyapeeth Institute of Technology, Kharghar",
      degree: "6th Semester",
      score: "96.35%"
    },
    {
      institution: "Bharati Vidyapeeth Institute of Technology, Kharghar",
      degree: "5th Semester",
      score: "93.78%"
    },
    {
      institution: "P.V.G'S Vidya Bhawan School, Nerul",
      degree: "SSC",
      score: "95.20%"
    }
  ];

  const interests = [
    {
      title: "Dancing",
      description: "Actively engages in dance performances, contributing vibrant energy to festivals and events."
    },
    {
      title: "Cooking",
      description: "Passionate about making different and delicious dishes."
    },
    {
      title: "Drawing & Rangoli",
      description: "Bagged few prizes, showcasing creativity and precision in every stroke."
    }
  ];

  return (
    <section className="about" id="about">
      <div className="about-header">
        <h2>About Me</h2>
        <p>Get to know me better</p>
      </div>

      <div className="about-content">
        <div className="education-section">
          <h3>Education</h3>
          <div className="timeline">
            {education.map((edu, index) => (
              <div className="timeline-item" key={index}>
                <div className="timeline-content">
                  <h4>{edu.institution}</h4>
                  <p className="degree">{edu.degree}</p>
                  <p className="score">Score: {edu.score}</p>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="interests-section">
          <h3>Interests</h3>
          <div className="interests-grid">
            {interests.map((interest, index) => (
              <div className="interest-card" key={index}>
                <h4>{interest.title}</h4>
                <p>{interest.description}</p>
              </div>
            ))}
          </div>
        </div>

        <div className="contact-section">
          <h3>Contact Details</h3>
          <div className="contact-info">
            <div className="contact-item">
              <i className="fas fa-envelope"></i>
              <p>siddhigawade.sbg25@gmail.com</p>
            </div>
            <div className="contact-item">
              <i className="fas fa-phone"></i>
              <p>8828271782</p>
            </div>
            <div className="contact-item">
              <i className="fas fa-map-marker-alt"></i>
              <p>Suyah CHS, Sec 21 Nerul, Navi Mumbai - 400706, Mumbai, India</p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
